package http.apache;


import org.apache.http.client.HttpClient;

import javax.xml.bind.DatatypeConverter;

public class ConcreteInjector {
    private final String hostAl = "https://10.10.30.115:8443";
    private final String hostBe = "https://10.10.30.115:8444";
    private final String hostCh = "http://10.10.30.115:8380";
    private final String hostDe = "http://10.10.30.115:8280";

    private final byte[] certAl = DatatypeConverter.parseBase64Binary( "" );
    private final byte[] certBe = DatatypeConverter.parseBase64Binary( "" );

    public ConcreteInjector() {
        init();
    }

    public void init() {
        try {
            HttpClientFactory.getInstance().getHttpClient( hostAl, certAl );
            HttpClientFactory.getInstance().getHttpClient( hostBe, certBe );
            HttpClientFactory.getInstance().getHttpClient( hostCh );
            HttpClientFactory.getInstance().getHttpClient( hostDe );

        } catch ( Exception e ) {
            throw new RuntimeException( e );
        }
    }

    public String getHostAl() { return this.hostAl; }
    public String getHostBe() { return this.hostBe; }
    public String getHostCh() { return this.hostCh; }
    public String getHostDe() { return this.hostDe; }

    public IInvoker getInvoker( String host ) {
        try {
            return new RestInvoker( HttpClientFactory.getInstance().getHttpClient( host ) );
        } catch ( Exception e ) {
            throw new RuntimeException( e );
        }
    }
}
